package arrayspack;
// program for getting the class named array in java
public class Classnamearray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int arr[]= {1,2,3}; // declaring and initializing array
Class c=arr.getClass(); // getting the class name
String name=c.getName();
System.out.println(name); // printing the class name integer type
System.out.println(c); // printing the name of class integer type
	}

}
